import React from 'react';
import { Route, Redirect } from 'react-router-dom';

export default ({ login, toregirect, component: Component, render: Render, data, ...rest }) => {
  return (
    <Route
      {...rest}
      render={props => (login ? <Component data={data} {...props}/> : <Redirect to={toregirect}/>)}/>
  );
}
