import {connect} from 'react-redux';
import Search from 'components/Search';
import userRequestActions from 'actions/userRequestActions';
import setTrueStatusPendingActions from 'actions/setTrueStatusPendingActions';
import getFetchDataActions from 'actions/getFetchDataActions';

const mapStateToProps = (state) => {
  return {
    state: state
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
    onChangeRequest: str => dispatch(userRequestActions(str)),
    onSetPending: () => dispatch(setTrueStatusPendingActions()),
    onGetData: (s) => dispatch(getFetchDataActions(s))
  }
};

const SearchsContainer = connect(
  mapStateToProps,
  mapDispatchToProps
)(Search);

export default SearchsContainer
