import {connect} from 'react-redux';
import Search from 'components/Search';
import userRequestActions from 'actions/userRequestActions';
import setTrueStatusRejectActions from 'actions/setTrueStatusRejectActions';
import setTrueStatusSuccessActions from 'actions/setTrueStatusSuccessActions';

// const mapStateToProps = (state) => {
//   return {
//     state: state
//   }
// };

const mapDispatchToProps = (dispatch) => {
  return {
    onChangeRequest: str => dispatch(userRequestActions(str)),
    onSetTrueReject: () => dispatch(setTrueStatusRejectActions()),
    onSetTrueSuccess: () => dispatch(setTrueStatusSuccessActions())
  }
};

const SearchsContainer = connect(
  null,
  mapDispatchToProps
)(Search);

export default SearchsContainer
