import {connect} from 'react-redux';
import Table from 'components/Table';
import setRowIDActions from 'actions/setRowIDActions';
import setTrueStatusRejectActions from "actions/setTrueStatusRejectActions";
import setTrueStatusSuccessActions from "actions/setTrueStatusSuccessActions";
import setDataActions from "actions/setDataActions";
import setDataParsedActions from "actions/setDataParsedActions";


const mapStateToProps = (state) => {
  return {
    state: state
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
    onSetID: id => dispatch(setRowIDActions(id)),
    onSetTrueReject: () => dispatch(setTrueStatusRejectActions()),
    onSetTrueSuccess: () => dispatch(setTrueStatusSuccessActions()),
    onSetData: () => dispatch(setDataActions()),
    onSetDataParsed: arr => dispatch(setDataParsedActions(arr))
  }
};

const TableContainer = connect(
  mapStateToProps,
  mapDispatchToProps
)(Table);

export default TableContainer
