export default (state = {}, action) => {
  switch (action.type) {
    case 'SET_TRUE_SUCCESS':
      return Object.assign({}, state, {
        successStatus: action.successStatus,
        pendingStatus: action.pendingStatus,
        rejectStatus: action.rejectStatus
      });
    case 'SET_TRUE_PENDING':
      return Object.assign({}, state, {
        successStatus: action.successStatus,
        pendingStatus: action.pendingStatus,
        rejectStatus: action.rejectStatus
      });
    case 'SET_TRUE_REJECT':
      return Object.assign({}, state, {
        successStatus: action.successStatus,
        pendingStatus: action.pendingStatus,
        rejectStatus: action.rejectStatus
      });
    default:
      return state;
  }
};
