const reducer = (state = {}, action) => {
  switch (action.type) {
    case 'USER_REQUEST':
      return Object.assign({}, state, {userRequest: action.userRequest});
    case 'USER_LOGIN':
      return Object.assign({}, state, {userLogin: action.userLogin});
    case 'SET_ROW_ID':
      return Object.assign({}, state, {id: action.id});
    case 'SET_TRUE_SUCCESS':
      return Object.assign({}, state, {
        successStatus: action.successStatus,
        pendingStatus: action.pendingStatus,
        rejectStatus: action.rejectStatus
      });
      case 'SET_TRUE_PENDING':
      return Object.assign({}, state, {
        successStatus: action.successStatus,
        pendingStatus: action.pendingStatus,
        rejectStatus: action.rejectStatus
      });
      case 'SET_TRUE_REJECT':
      return Object.assign({}, state, {
        successStatus: action.successStatus,
        pendingStatus: action.pendingStatus,
        rejectStatus: action.rejectStatus
      });



    default:
      return state;
  }
};

export default reducer;

